root.script:
Game.Subscribes.MoneyChanged.push(invokerpanelr)
var Abilities = $('#Abilities').Children()
var MyEnt = Players.GetPlayerHeroEntityIndex(Game.GetLocalPlayerID())
function invokerpanelr(){
	var InvokeCD = Abilities.GetCooldownTimeRemaining(Entities.GetAbilityByName(MyEnt, 'invoker_invoke'))
	for (i = 0; i <= 9; i++) {
		var Ab = Entities.GetAbilityByName(MyEnt, Abilities[i].Children()[0].Children()[0].abilityname)
		var ManaCost = Abilities.GetManaCost(Ab)
		var Cooldown = Abilities.GetCooldownTimeRemaining(Ab)
		Abilities[i].Children()[1].text = ManaCost
		if (Cooldown == 0){
			Abilities[i].Children()[0].Children()[0].RemoveClass('OnCD')
			Abilities[i].Children()[0].Children()[1].text = ''
		} else {
			Abilities[i].Children()[0].Children()[0].AddClass('OnCD')
			Abilities[i].Children()[0].Children()[1].text = Math.round(Cooldown)
		}
	}
}

function Cast( a, b, c ){
	Abilities.ExecuteAbility( Entities.GetAbility( MyEnt, a ), MyEnt, false )
	Abilities.ExecuteAbility( Entities.GetAbility( MyEnt, b ), MyEnt, false )
	Abilities.ExecuteAbility( Entities.GetAbility( MyEnt, c ), MyEnt, false )
	Abilities.ExecuteAbility( Entities.GetAbility( MyEnt, 5 ), MyEnt, false ) 
}

function invoker_cold_snap(){
	Cast(0,0,0)
}
function invoker_ghost_walk(){
	Cast(0,0,1)
}
function invoker_ice_wall(){
	Cast(0,0,2)
}
function invoker_emp(){
	Cast(1,1,1)
}
function invoker_tornado(){
	Cast(1,1,0)
}
function invoker_alacrity(){
	Cast(1,1,2)
}
function invoker_sun_strike(){
	Cast(2,2,2)
}
function invoker_forge_spirit(){
	Cast(2,2,0)
}
function invoker_chaos_meteor(){
	Cast(2,2,1)
}
function invoker_deafening_blast(){
	Cast(0,1,2) 
}
